const CONTENT_BASE="content";function escapeHtml(n){const t=document.createElement("div");return t.textContent=n,t.innerHTML}async function fetchJSON(n){const t=await fetch(`content/${n}`);if(!t.ok)throw new Error(`Failed to load ${n}`);return t.json()}async function fetchText(n){const t=await fetch(`content/${n}`);if(!t.ok)throw new Error(`Failed to load ${n}`);return t.text()}async function fetchDisciplines(){const n=await fetchJSON("disciplines.json");return Array.isArray(n)?n.sort((n,t)=>(n.order||0)-(t.order||0)):[]}async function fetchLessons(){const n=await fetchJSON("lessons.json");return Array.isArray(n)?n:[]}function getLessonsByDiscipline(n,t){return n.filter(n=>n.discipline===t).sort((n,t)=>(n.order||0)-(t.order||0))}function getLesson(n,t,e){return n.find(n=>n.discipline===t&&n.slug===e)}function getDiscipline(n,t){return n.find(n=>n.slug===t)}async function fetchLessonMarkdown(n,t){return fetchText(`${n}/${t}`)}