const REVIEWED_STORAGE_KEY="iss-reviewed-exercises";function getReviewedExerciseIds(){try{const e=localStorage.getItem(REVIEWED_STORAGE_KEY),t=e?JSON.parse(e):[];return Array.isArray(t)?new Set(t):new Set}catch{return new Set}}function markExerciseAsReviewed(e){const t=getReviewedExerciseIds();if(!t.has(e)){t.add(e);try{localStorage.setItem(REVIEWED_STORAGE_KEY,JSON.stringify([...t]))}catch(e){}}}